    <style>
      :root {
    --black: #000;
    --black1: #0b0b0c;
    --dk-black:#202020;
    --dk-white:#fff;
    --black2: rgb(18, 18, 18);
    --black3: #221f1f;
    --gray: #666;
    --red: #ef3a3d;
    --white2: #d9d9d9;
    --white: #fff;
    --gold: #f0131e;
    --gold1: #f0131e;

    --foreground:var(--dk-black);
    --background:var(--dk-white);
}
.darkmode{
    --black: #000;
    --black1: #0b0b0c;
    --dk-black:#202020;
    --dk-white:#fff;
    --black2: rgb(18, 18, 18);
    --black3: #221f1f;
    --gray: #666;
    --red: #ef3a3d;
    --white2: #d9d9d9;
    --white: #fff;
    --gold: #f0131e;
    --gold1: #f0131e;

    --foreground:var(--dk-white);
    --background:var(--dk-black);
}
::-webkit-scrollbar {
    width: 8px;
    
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(var(--black), var(--black2));
    border: 1px solid var(--white);
    border-radius: 6px;
    
}
      .bd-placeholder-img {
        font-size: 1.125rem;
        text-anchor: middle;
        -webkit-user-select: none;
        -moz-user-select: none;
        user-select: none;
      }

      @media (min-width: 768px) {
        .bd-placeholder-img-lg {
          font-size: 3.5rem;
        }
      }

      .b-example-divider {
        width: 100%;
        height: 3rem;
        background-color: rgba(0, 0, 0, .1);
        border: solid rgba(0, 0, 0, .15);
        border-width: 1px 0;
        box-shadow: inset 0 .5em 1.5em rgba(0, 0, 0, .1), inset 0 .125em .5em rgba(0, 0, 0, .15);
      }

      .b-example-vr {
        flex-shrink: 0;
        width: 1.5rem;
        height: 100vh;
      }

      .bi {
        vertical-align: -.125em;
        fill: currentColor;
      }

      .nav-scroller {
        position: relative;
        z-index: 2;
        height: 2.75rem;
        overflow-y: hidden;
      }

      .nav-scroller .nav {
        display: flex;
        flex-wrap: nowrap;
        padding-bottom: 1rem;
        margin-top: -1px;
        overflow-x: auto;
        text-align: center;
        white-space: nowrap;
        -webkit-overflow-scrolling: touch;
      }

      .btn-bd-primary {
        --bd-violet-bg: #f0131e;
        --bd-violet-rgb: #f0131e;

        --bs-btn-font-weight: 600;
        --bs-btn-color: var(--bs-white);
        --bs-btn-bg: var(--bd-violet-bg);
        --bs-btn-border-color: var(--bd-violet-bg);
        --bs-btn-hover-color: var(--bs-white);
        --bs-btn-hover-bg: #f0131e;
        --bs-btn-hover-border-color: #f0131e;
        --bs-btn-focus-shadow-rgb: var(--bd-violet-rgb);
        --bs-btn-active-color: var(--bs-btn-hover-color);
        --bs-btn-active-bg: #a31219;
        --bs-btn-active-border-color: #a31219;
      }
      .bd-mode-toggle {
        z-index: 1500;
      }
    </style>

    
